var PersistanceManager = function PersistanceManager(session)
{
    this.pseudo = null ;
    this.session = session ;
    this.savePseudo = function(pseudo)
    {
        this.session.pseudo = pseudo ;
        this.pseudo = pseudo ;
    }

    this.getPseudo = function()
    {
        return this.session.pseudo ;
    }

}

exports.PersistanceManager = PersistanceManager ;